import { Tab } from '@headlessui/react';
import HeadlessTippy from '@tippyjs/react/headless';
import classNames from 'classnames/bind';

import { Image, Button, Popper } from '~/components';
import images from '~/assets/images';
import styles from './BoxContainer.module.scss';

const cx = classNames.bind(styles);

const tabs = [
    { id: 0, title: 'Hotels' },
    { id: 1, title: 'Flights' },
    { id: 2, title: 'Train Tickets' },
    { id: 3, title: 'Saving Combos' },
];

const options_hotel = [
    {
        id: 0,
        title: 'Check-in date',
        rightIcon: <Image src={images.caretDownPng} alt="caret-down" />,
        leftIcon: <Image src={images.calendarIcon} alt="user-png" />,
        items: [
            {
                id: 0,
                title: '12/11/2022',
                isActive: true,
            },
            {
                id: 1,
                title: '13/11/2022',
            },
            {
                id: 2,
                title: '14/11/2022',
            },
            {
                id: 3,
                title: '15/11/2022',
            },
            {
                id: 4,
                title: '16/11/2022',
            },
            {
                id: 5,
                title: 'Show more',
            },
        ],
    },
    {
        id: 1,
        title: 'Quantity',
        rightIcon: <Image src={images.caretDownPng} alt="caret-down" />,
        leftIcon: <Image src={images.userPng} alt="user-png" />,
        items: [
