                                <div>
                                    {options_hotel.map((option) => (
                                        <HeadlessTippy
                                            key={option.id}
                                            interactive
                                            offset={[0, 5]}
                                            placement="bottom-start"
                                            trigger="click"
                                            render={(attrs) => (
                                                <div tabIndex="-1" {...attrs}>
                                                    <Popper className="py-3 min-w-[14rem]">
